$(document).ready(function() {
    var tagIndex = 0;

    $('#addTagButton').click(function() {
        tagIndex++;
        var newTagDiv = `
            <div class="input-group mb-3">
                <span class="input-group-text">&#128278;</span>
                <div class="form-floating">
                    <input type="text" class="form-control" id="floatingInputGroup${tagIndex}" name="newTag[]" placeholder="Add a new tag">
                    <label for="floatingInputGroup${tagIndex}" class="form-label">New category</label>
                </div>
            </div>`;
        $('#dynamicTagsContainer').append(newTagDiv);
    });
});
